const initialState = {
    cart_product:[]
};

export default function(state = initialState, action ) {
    switch(action.type) {
        case 'ADD_TO_CART':
            return {
                ...state,
                cart_product:[...state.cart_product,action.payload]
            };
        case 'ADD_AMOUNT_PRODUCT':
            let data = action.payload
            // state.cart_product[data[0]].count = data[1]
            let newDict = state.cart_product.filter(el=>{
                return el.id === data[0]
                el.count = data[1]
            })

            console.log(data,state.cart_product,newDict)
            return {
                ...state,
                cart_product:state.cart_product
            };
        default: 
            return state;
    }
}
