import React,{Component} from 'react';
import {Container,Row,Col,Card,Button} from 'react-bootstrap'
import {data} from '../dummydb';

class mycart extends Component{
    constructor(){
        super()
        this.state={
            amount:1,
        }
    }

    handleChange(e){
        this.setState({amount:e.target.value})
    }

    addAmount(){
        this.setState({amount:this.state.amount+1})
    }
    minAmount(){
        this.setState({amount:this.state.amount-1})
    }

    render(){
        return(
            <Container style={{paddingTop:40,maxWidth:1024}}>

                <Card className="m-b-20">

                    <Card.Body>
                        <Row>
                        <Col md={3}>
                            <img alt='datacart' width={"100%"} height={200} src={data[3].image_url} />
                        </Col>
                        <Col md={9}>
                            <h3>{data[3].name}</h3>
                            <p>Rp. {data[3].price}</p>
                            <p>Jumlah</p>
                            <Row>
                                <Col>
                                <Button variant="danger"
                                    onClick={this.minAmount.bind(this)}
                                ><i className="fa fa-minus"></i></Button>
                                    <input value={this.state.amount} 
                                    onChange={this.handleChange.bind(this)}
                                    type="text" className="input-cart-amount"></input>
                                <Button 
                                    onClick={this.addAmount.bind(this)}
                                ><i className="fa fa-plus"></i></Button>
                                </Col>
                                <Col className="text-right" style={{paddingTop:20}}>
                                    <p>Rp {this.state.amount*data[3].price}</p>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                    </Card.Body>
                </Card>

                <Card>
                    <Card.Body>
                        <Row>
                            <Col md={3}>
                                Total Belanja : 
                            </Col>
                            <Col md={9} className="text-right">
                                <b>Rp. 9000000</b>
                            </Col>
                        </Row>
                    </Card.Body>
                </Card>

            </Container>
        );
    }
}

export default mycart;
