export const addToCart = (data) => dispatch => {
    dispatch({
        type: 'ADD_TO_CART',
        payload: data
    });

}

export const addAmount = (data) => dispatch =>{
    this.props.cart.cart_product.map(item=>{
        if(item.id === data[0]){
            item.count = data[1]
        }
    })

    dispatch({
        type:'ADD_AMOUNT_PRODUCT',
        payload:this.props.cart.cart_product
    })
}

export const minAmount = (data) => dispatch =>{
    dispatch({
        type:'MIN_AMOUNT_PRODUCT',
        payload:data
    })
}
