import React,{Component} from 'react';
import {Row,Col,Container,Card,Button} from 'react-bootstrap';
import {Link} from 'react-router-dom';

class home extends Component{

    constructor(){
        super();
        this.state={
            phone:[
                {
                    id:1,
                    name:"Iphone X",
                    price:12000000,
                    image_url:"https://images-na.ssl-images-amazon.com/images/I/514YMVAc6NL._SL1024_.jpg",
                    deskripsi:"Lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum",
                    rating:5
                },
                {
                    id:2,
                    name:"Sammsung S10",
                    price:10500000,
                    image_url:"https://images-na.ssl-images-amazon.com/images/I/61YVqHdFRxL._SX569_.jpg",
                    deskripsi:"Lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum",
                    rating:5
                },
                {
                    id:3,
                    name:"Oppo F9",
                    price:8500000,
                    image_url:"http://www.directd.com.my/images/thumbs/0024050_oppo-f9-original-set-freebies-hotel-voucher-ready-stock_600.jpeg",
                    deskripsi:"Lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum",
                    rating:4
                },
                {
                    id:4,
                    name:"Redmi Note 7",
                    price:7000000,
                    image_url:"https://images-na.ssl-images-amazon.com/images/I/41FBnbqW3pL.jpg",
                    deskripsi:"Lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum",
                    rating:3
                },
                {
                    id:5,
                    name:"Vivo v15",
                    price:7500000,
                    image_url:"https://static.toiimg.com/thumb/msid-68742772,width-640,resizemode-4/68742772.jpg",
                    deskripsi:"Lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum",
                    rating:4
                }
            ]
        }
    }
    render(){

        return(
            <>
                <Container>
                    <h2 className="text-center " style={{margin:20}}>Produk kami</h2>
                    <Row >
                        {this.state.phone.map((item,key)=>{
                            return <Col key={key} md={3} style={{paddingTop:20,paddingBottom:20}}>
                            <Card style={{maxHeight:"400px"}} className="custom-href">
                                <Link to={"/detail/"+item.id}>
                                    <Card.Img variant="top" src={item.image_url} width="100%" height="200px" />
                                </Link>
                                    <Card.Body>
                                    <Link to={"/detail/"+item.id}>
                                            <Card.Title>{item.name}</Card.Title>
                                            <Card.Text>
                                            Rp. {item.price}
                                            </Card.Text>
                                    </Link>
                                    <Button variant="primary"><i className="fa fa-shopping-cart"></i></Button>
                                </Card.Body>

                                </Card>
                            </Col>
                        })}
                    </Row>
                </Container>
            </>
        );
    }
}

export default home;
